local targetEnabled = true-- Function to trigger the dpemotes slap emotelocal function playSlapEmote()    ExecuteCommand('nearby slap')  -- This will trigger the slap emote from dpemotesend-- Function to trigger the dpemotes rose emotelocal function playRoseEmote()    ExecuteCommand('e rose')  -- This will trigger the rose emote from dpemotesend-- Function to trigger the shared hug emote using the /nearby commandlocal function playHugEmote()    ExecuteCommand('nearby hug')  -- This will trigger the shared hug emote from dpemotesend-- Function to trigger the dpemotes namaste emotelocal function playNamasteEmote()    ExecuteCommand('e namaste')  -- This will trigger the namaste emote from dpemotesend-- Function to add target interaction for all playerslocal function addPlayerInteraction()    local players = GetActivePlayers()    for _, playerId in ipairs(players) do        local playerPed = GetPlayerPed(playerId)        exports['qb-target']:AddTargetEntity(playerPed, {            options = {                {                    type = "client",                    event = "interaction:slapPlayer", -- Trigger the slap event                    icon = "fas fa-hand",                    label = "Slap Player",                },                {                    type = "client",                    event = "interaction:giveRose", -- Trigger the give rose event                    icon = "fas fa-rose",                    label = "Give Rose",                },                {                    type = "client",                    event = "interaction:hugPlayer", -- Trigger the hug event                    icon = "fas fa-hands-helping",                    label = "Hug Player",                },                {                    type = "client",                    event = "interaction:namastePlayer", -- Trigger the namaste event                    icon = "fas fa-praying-hands",                    label = "Namaste",                }            },            distance = 2.0, -- Maximum distance for interaction        })    endend-- Call the player interaction function when the script is initializedCitizen.CreateThread(function()    addPlayerInteraction() -- Add interaction for all players when the script startsend)-- Event handler for slapping a playerRegisterNetEvent('interaction:slapPlayer')AddEventHandler('interaction:slapPlayer', function()    -- Play the slap emote from dpemotes    playSlapEmote()    -- Notify the slapping player    TriggerEvent('chat:addMessage', {        color = {255, 0, 0},        multiline = true,        args = {"Interaction", "You slapped the player!"}    })end)-- Event handler for giving a rose to a playerRegisterNetEvent('interaction:giveRose')AddEventHandler('interaction:giveRose', function()    -- Play the rose emote from dpemotes    playRoseEmote()    -- Notify the player who gave the rose    TriggerEvent('chat:addMessage', {        color = {255, 0, 255},        multiline = true,        args = {"Interaction", "You gave a rose!"}    })end)-- Event handler for hugging a playerRegisterNetEvent('interaction:hugPlayer')AddEventHandler('interaction:hugPlayer', function()    -- Play the shared hug emote using the /nearby (hug) command    playHugEmote()    -- Notify the player who initiated the hug    TriggerEvent('chat:addMessage', {        color = {0, 255, 255},        multiline = true,        args = {"Interaction", "You initiated a hug!"}    })end)-- Event handler for performing namaste with a playerRegisterNetEvent('interaction:namastePlayer')AddEventHandler('interaction:namastePlayer', function()    -- Play the namaste emote from dpemotes    playNamasteEmote()    -- Notify the player who performed namaste    TriggerEvent('chat:addMessage', {        color = {0, 255, 0},        multiline = true,        args = {"Interaction", "You performed Namaste!"}    })end)